mapper.select:
SELECT DISTINCT r.id, r.name, r.code, r.application_name, r.type, r.version, r.value, r.authority, r.icon, r.parent_id, r.status, r.sort, r.creation_time, r.source, r.remark FROM tb_resource r LEFT JOIN tb_group_resource gr ON gr.resource_id = r.id LEFT JOIN tb_group g ON gr.group_id = g.id LEFT JOIN tb_group_console_user gu ON gu.group_id = g.id LEFT JOIN tb_console_user u ON gu.user_id = u.id WHERE (u.id = #{userId} OR r.id in ( SELECT ur.resource_id FROM tb_console_user_resource ur WHERE ur.user_id = #{userId} )) AND r.status = 1
AND r.source IN
#{s}
AND r.type = #{filter.typeEq}
ORDER BY r.sort
