INSERT INTO `his_config_info` VALUES (10, 50, 'admin.yml', 'SG', '', 'spring:\n  security:\n    support:\n      audit:\n        type: elasticsearch\n  task:\n    execution:\n      pool:\n        # 线程池大小。默认为 1 ，根据自己应用来设置\n        core-size: 128\n      shutdown:\n        # 应用关闭时，是否等待定时任务执行完成。默认为 false ，建议设置为 true\n        await-termination: true\n  datasource:\n    url: jdbc:mysql://localhost:3306/sg_admin?autoReconnect=true&useUnicode=true&characterEncoding=UTF-8&useSSL=false\n    driver-class-name: com.mysql.jdbc.Driver\n    username: root\n    password: \n    type: com.zaxxer.hikari.HikariDataSource\n  redis:\n    host: localhost\n    port: 6379\n  rabbitmq:\n    host: localhost\n    port: 5672\n    username: admin\n    password: admin\n    publisher-returns: true\n    publisher-confirm-type: correlated\n    listener:\n      direct:\n        acknowledge-mode: manual\n      simple:\n        acknowledge-mode: manual\n        retry:\n          initial-interval: 45000\n          max-interval: 80000\n          enabled: true\n    cache:\n      channel:\n        size: 100', 'f9b43f2fea71651cc477f8fa4b088185', '2021-08-18 09:58:32', '2021-08-18 01:58:32', NULL, '0:0:0:0:0:0:0:1', 'D', '');
INSERT INTO `his_config_info` VALUES (13, 51, 'gateway.yml', 'SG', '', 'spring:\n  application:\n    crypto:\n      access:\n        algorithm-mode:\n          algorithm: \"AES\"\n          mode: \"CBC\"\n          block-size: 0\n          padding-scheme: \"PKCS5\"\n          streaming-mode: \"CBC\"\n          streaming-block-size: 0\n          streaming-padding-scheme: \"PKCS5\"\n          keySize: 128\n          initialization-vector-size: 128\n  redis:\n    host: localhost\n    port: 6379\n  cloud:\n    gateway:\n      routes:\n        # 配置 socketio 路由\n        - id: socket\n          predicates:\n            - Path=/socket.io/**\n          uri: lb://socket\n      discovery:\n        locator:\n          enabled: true\n          predicates:\n            - name: Path\n              args:\n                pattern: \"\'/\'+serviceId+\'/**\'\"\n#            - name: Header\n#              args:\n#                header: \"\'X-FILTER-RESULT-ID\'\"\n#                regexp: \"/^\\s*$/\"\n          filters:\n            - name: RewritePath\n              args:\n                regexp: \"\'/\' + serviceId + \'/(?<remaining>.*)\'\"\n                replacement: \"\'/${remaining}\'\"\n            - name: AddRequestHeader\n              args:\n                name: \"\'X-REQUEST-CLIENT\'\"\n                value: \"\'SPRING_GATEWAY\'\"\n    sentinel:\n      transport:\n        port: 8787\n        dashboard: localhost:8787\n      eager: true\n      datasource:\n        gateway-api-group-api-group:\n          nacos:\n            server-addr: ${spring.cloud.nacos.discovery.server-addr}\n            data-id: sentinel-gateway-api-group.json\n            group-id: SG\n            rule-type: GW_API_GROUP', 'f7a280be3ec6bd4b8c885d8d27ac6afe', '2021-08-18 09:58:32', '2021-08-18 01:58:32', NULL, '0:0:0:0:0:0:0:1', 'D', '');
