INSERT INTO `his_config_info` VALUES (19, 54, 'message.yml', 'SG', '', 'sg:\n  message:\n    # 持久化消息大小，最多保留多少条信息\n    persistent-size: 100\n    # 州频道名称\n    continent-room-name: \"本州\"\n    # 默认房间配置\n    default-room:\n      - code: \"world\"\n        event: \"WORLD_MESSAGE_EVENT\"\n        name: \"世界\"\n\nspring:\n  security:\n    support:\n      cache:\n        name: \"sg:game:socket:server:spring:security:context:\"\n        expiresTime:\n          value: 7\n          unit: DAYS\n  datasource:\n    url: jdbc:mysql://localhost:3306/sg_message?autoReconnect=true&useUnicode=true&characterEncoding=UTF-8&useSSL=false\n    driverClassName: com.mysql.jdbc.Driver\n    username: root\n    password: \n    type: com.zaxxer.hikari.HikariDataSource\n  redis:\n    host: localhost\n    port: 6379', 'c2401b3edf0399c9450a8a76f2cecd65', '2021-08-18 09:58:38', '2021-08-18 01:58:38', NULL, '0:0:0:0:0:0:0:1', 'D', '');
INSERT INTO `his_config_info` VALUES (23, 55, 'user.yml', 'SG_USER', '', 'sg:\n  # 应用配置\n  app:\n    # 管理后台 url\n    admin-rest-url-prefix: \"http://localhost:8080/admin/\"\n    # 雪花 id 配置\n    snowflake:\n      # 数据中心编号，最大支持数据中心节点数 0~31，一共 32 个\n      serviceId: \"002\"\n      # 服务编号（业务编号），共3位以字符串组成，000~999，共1000个\n      workerId: 1\n      # 工作机器编号，最大支持机器节点数 0~31，一共 32 个\n      dataCenterId: 1\n  # 用户配置\n  user:\n    # 玩家缓存配置\n    player-user-cache:\n      name: \"game:sg:user:cache:player:\"\n      expires-time:\n        value: 7\n        unit: DAYS\n    # 账户缓存配置\n    account-user-cache:\n      name: \"game:sg:user:cache:account:\"\n      expires-time:\n        value: 7\n        unit: DAYS\n\nspring:\n  security:\n    support:\n      cache:\n        name: \"sg:game:socket:server:spring:security:context:\"\n        expires-time:\n          value: 7\n          unit: DAYS\n  datasource:\n    url: jdbc:mysql://localhost:3306/sg_user?autoReconnect=true&useUnicode=true&characterEncoding=UTF-8&useSSL=false\n    driver-class-name: com.mysql.jdbc.Driver\n    username: root\n    password:\n    type: com.zaxxer.hikari.HikariDataSource\n  redis:\n    host: localhost\n    port: 6379', 'a08d17df8dfc374258c5ce82e687f27a', '2021-08-18 09:58:38', '2021-08-18 01:58:38', NULL, '0:0:0:0:0:0:0:1', 'D', '');
INSERT INTO `his_config_info` VALUES (24, 56, 'map.yml', 'SG_USER', '', 'sg:\n  # 地图配置\n  map:\n    # 同步后台数据 cron 表达式\n    sync-admin-data-cron: 0 * * * * ?\n    # 使用管理后台的哪个地图\n    id: 1\n    # 缓存信息\n    cache:\n      name: \"game:sg:map:cache:\"\n    # 配置\n    view-size:\n      # 城池视野\n      city: 5\n      # 屏幕视野\n      screen: 15', 'c52b76dcd535213cdd67147acb6aef87', '2021-08-18 09:58:44', '2021-08-18 01:58:45', NULL, '0:0:0:0:0:0:0:1', 'D', '');
INSERT INTO `his_config_info` VALUES (25, 57, 'fortress.yml', 'SG_USER', '', 'sg:\n  # 要塞配置\n  fortress:\n    # 最大部队数量\n    max-army-count: 5\n    # 耐久配置\n    durable:\n      # 默认耐久值\n      default-value: 1000\n      # 最大耐久值\n      max-value: 1000\n      # 增长计数\n      increment-value: 0.000006\n      # 恢复系数，恢复公式为: 当前建筑的最大耐久 * 增长计数 * 恢复系数 * 首次减少耐久时间与当前时间间隔的秒数\n      # 例子：耐久度为 10000 的城池，则每分钟恢复耐久 = 10000 * 0.000006(increasing-value) * (1(recovery-coefficient) * 60) = 3\n      # 恢复满值的时间为 46 小时和率土鸿途类似\n      recovery-coefficient: 1\n    # 创建城池的等级配置\n    level:\n      1:\n        # 完成用时\n        complete-time:\n          value: 6\n          unit: HOURS\n        # 令和交易支出配置\n        decreeTransaction:\n          # 支出的令\n          decree: 3\n          # 支出配置\n          transaction:\n            - type: Money\n              amount: 10000\n              transactionInfo: CreateOrUpgradeFortress\n              remark: \"创建 [{0}] 要塞\"\n      2:\n        # 完成用时\n        complete-time:\n          value: 1\n          unit: HOURS\n        # 令和交易支出配置\n        decreeTransaction:\n          # 支出的令\n          decree: 1\n          # 支出配置\n          transaction:\n            - type: Money\n              amount: 5000\n              transactionInfo: CreateOrUpgradeFortress\n              remark: \"升级 [{0}] 要塞\"\n      3:\n        # 完成用时\n        complete-time:\n          value: 1\n          unit: HOURS\n        # 令和交易支出配置\n        decreeTransaction:\n          # 支出的令\n          decree: 1\n          # 支出配置\n          transaction:\n            - type: Money\n              amount: 5000\n              transactionInfo: CreateOrUpgradeFortress\n              remark: \"升级 [{0}] 要塞\"\n      4:\n        # 完成用时\n        complete-time:\n          value: 1\n          unit: HOURS\n        # 令和交易支出配置\n        decreeTransaction:\n          # 支出的令\n          decree: 1\n          # 支出配置\n          transaction:\n            - type: Money\n              amount: 5000\n              transactionInfo: CreateOrUpgradeFortress\n              remark: \"升级 [{0}] 要塞\"\n      5:\n        # 完成用时\n        complete-time:\n          value: 1\n          unit: HOURS\n        # 令和交易支出配置\n        decreeTransaction:\n          # 支出的令\n          decree: 1\n          # 支出配置\n          transaction:\n            - type: Money\n              amount: 5000\n              transactionInfo: CreateOrUpgradeFortress\n              remark: \"升级 [{0}] 要塞\"', '126814358b0ea63833cfcf19bf2ce891', '2021-08-18 09:58:44', '2021-08-18 01:58:45', NULL, '0:0:0:0:0:0:0:1', 'D', '');
INSERT INTO `his_config_info` VALUES (26, 58, 'city.yml', 'SG_USER', '', 'sg:\n  # 城池配置\n  city:\n    # 同步后台数据 cron 表达式\n    sync-admin-data-cron: 0 * * * * ?\n    # 最大部队数量\n    max-army-count: 5\n    # 默认可创建城池的地图等级\n    territory-level: 1\n    # 耐久配置\n    durable:\n      # 默认耐久值\n      default-value: 1000\n      # 最大耐久值\n      max-value: 1000\n      # 增长计数\n      increment-value: 0.000006\n      # 恢复系数，恢复公式为: 当前建筑的最大耐久 * 增长计数 * 恢复系数 * 首次减少耐久时间与当前时间间隔的秒数\n      # 例子：耐久度为 10000 的城池，则每分钟恢复耐久 = 10000 * 0.000006(increasing-value) * (1(recovery-coefficient) * 60) = 3\n      # 恢复满值的时间为 46 小时和率土鸿途类似\n      recovery-coefficient: 1\n    # 创建城池的等级配置\n    level:\n      1:\n        # 完成用时\n        complete-time:\n          value: 6\n          unit: HOURS\n        # 令和交易支出配置\n        decree-transaction:\n          # 支出的令\n          decree: 6\n          # 支出配置\n          transaction:\n            - type: Money\n              amount: 60000\n              transactionInfo: CreateSubCity\n              remark: \"创建 [{0}] 分城\"\n    # 管理后台的城池内设建筑缓存配置\n    city-building-cache:\n      name: \"game:sg:city:building:cache:\"\n    # 用户城池缓存配置\n    user-city-cache:\n      name: \"game:sg:user:city:cache:\"\n      expires-time:\n        value: 7\n        unit: DAYS', 'deff42b6f7ecf1a58f574a6e70714c01', '2021-08-18 09:58:44', '2021-08-18 01:58:45', NULL, '0:0:0:0:0:0:0:1', 'D', '');
INSERT INTO `his_config_info` VALUES (27, 59, 'army.yml', 'SG_USER', '', 'sg:\n  # 部队配置\n  army:\n    # 涉及部队的建筑代码\n    army-building-code: \"yanwutai\"\n    # 卡槽配置\n    card:\n      # 前锋类型的卡槽配置\n      - type: 10\n        # 默认不启用，如果表达式通过，就启用\n        enable: 0\n        # 根据内部变量获取出来的城池内建设施等级判断，如果该等级大于用户城池的前锋数量，才能启用。\n        expression: \'#innerBuilding.level >= #army.sort\'\n        remark: \"\'司令台 Lv.\' + #army.sort + \' 级启用\'\"\n      # 中军类型的卡槽配置\n      - type: 20\n        # 默认启用\n        enable: 1\n      # 大营类型的卡槽配置\n      - type: 30\n        # 默认启用\n        enable: 1', '537bfbc4c18568b9baa36dc4114589a0', '2021-08-18 09:58:44', '2021-08-18 01:58:45', NULL, '0:0:0:0:0:0:0:1', 'D', '');
INSERT INTO `his_config_info` VALUES (28, 60, 'card.yml', 'SG_CARD', '', 'sg:\n  # 应用配置\n  app:\n    # admin rest url 前缀\n    admin-rest-url-prefix: \"http://localhost:8080/admin/\"\n    # 雪花 id 配置\n    snowflake:\n      # 数据中心编号，最大支持数据中心节点数 0~31，一共 32 个\n      data-center-id: 1\n      # 服务编号（业务编号），共3位以字符串组成，000~999，共1000个\n      service-id: \"001\"\n      # 工作机器编号，最大支持机器节点数 0~31，一共 32 个\n      worker-id: 1\n  # 卡片配置\n  card:\n    # 同步后台数据 cron 表达式\n    sync-admin-data-cron: 0 * * * * ?\n    # 默认最大卡片总数\n    max-card-number: 200\n    # 锁定星级\n    lock-star-level: 5\n    # 默认卡片创建体力\n    default-strength: 150\n    # 默认卡片创建最大体力\n    default-max-strength: 150\n    # 卡片缓存配置\n    card-cache:\n      name: \"game:sg:card:cache:map\"\n    # 属性点配置\n    attr:\n      # 免费洗点次数\n      free-clean-number: 1\n      # 洗点支付配置\n      clean-pay:\n        - type: Rune\n          amount: 20\n          transactionInfo: CleanUserCardAttrValue\n          remark: \"对武将 [{0}] 进行洗点\"\n      # 男性卡牌的属性加点值\n      male-add-value: 10\n      # 女性卡牌的属性加点值\n      female-add-value: 15\n      # 属性点增加等级\n      add-leve-lvalue: 10\n    # 转换经验技能值配置\n    conversion:\n      # 最大批量转换个数\n      max-batch-count: 1000\n      # 转换战法经验值配置\n      exp:\n        1: 3\n        2: 6\n        3: 12\n        4: 24\n        5: 36\n      double-probability: 0.8\n      # 支付配置\n      pay:\n        - type: Rune\n          amount: 5\n          transactionInfo: ConversionSkillExp\n          remark: \"对武将 [{0}] 进行兵法经验转换\"\n        - type: Money\n          amount: 20\n          transactionInfo: ConversionSkillExp\n          remark: \"对武将 [{0}] 进行兵法经验转换\"\n    # 卡片经验值配置\n    exp:\n      # 攻击用户城池经验加成公式\n      attackUserCityFormula: \"\"\n      # 攻击用户拒马经验加成公式\n      attackResistingFormula: \"\"\n      # 攻击用户土地经验加成公式\n      attackUserTerritoryFormula: \"#defenceMaxLevelCard.level * (#defenceMaxStarLevelCard.starLevel + 3) / 120D * #defenceBattleReportArmy.hpTotalMax * 1\"\n      # 攻击野生城池经验加成公式\n      attackCityFormula: \"\"\n      # 攻击土地经验加成公式\n      attackTerritoryFormula: \"#defenceMaxLevelCard.level * (#defenceMaxStarLevelCard.starLevel + 3) / 120D * #defenceBattleReportArmy.hpTotalMax * 1\"\n      # 玩家碰撞经验加成公式\n      userCollisionFormula: \"#defenceMaxLevelCard.level * (#defenceMaxStarLevelCard.starLevel + 3) / 120D * #defenceBattleReportArmy.hpTotalMax * 0.5\"\n      # 攻击联盟城池经验加成公式\n      leagueCityFormula: \"\"\n      # 卡片等级顺序该配置为范围值，使用逗号分隔，逗号前面是范围开始值，后面是范围结束值\n      level-range:\n        1: \'0,6\'\n        2: \'7,16\'\n        3: \'17,116\'\n        4: \'117,1016\'\n        5: \'1017,2966\'\n        6: \'2967,5406\'\n        7: \'5407,9056\'\n        8: \'9057,17566\'\n        9: \'17567,28506\'\n        10: \'28507,40666\'\n        11: \'40667,54666\'\n        12: \'54667,71666\'\n        13: \'71667,91666\'\n        14: \'91667,116166\'\n        15: \'116167,143166\'\n        16: \'143167,173666\'\n        17: \'173667,204666\'\n        18: \'204667,236166\'\n        19: \'236167,268166\'\n        20: \'268167,300506\'\n        21: \'300507,338846\'\n        22: \'338847,382186\'\n        23: \'382187,432326\'\n        24: \'432327,496326\'\n        25: \'496327,571326\'\n        26: \'571327,661326\'\n        27: \'661327,761226\'\n        28: \'761227,871116\'\n        29: \'871117,991986\'\n        30: \'991987,1130986\'\n        31: \'1130987,1290836\'\n        32: \'1290837,1482656\'\n        33: \'1482657,1712836\'\n        34: \'1712837,1989046\'\n        35: \'1989047,2298396\'\n        36: \'2298397,2644866\'\n        37: \'2644867,3032906\'\n        38: \'3032907,3467506\'\n        39: \'3467507,3917106\'\n        40: \'3917107,4459806\'\n        41: \'4459807,5157306\'\n        42: \'5157307,6048826\'\n        43: \'6048827,7173826\'\n        44: \'7173827,8558826\'\n        45: \'8558827,10478426\'\n        46: \'10478427,12734426\'\n        47: \'12734427,15346426\'\n        48: \'15346427,18334426\'\n        49: \'18334427,21718426\'\n\nspring:\n  security:\n    support:\n      cache:\n        name: \"sg:game:socket:server:spring:security:context:\"\n        expires-time:\n          value: 7\n          unit: DAYS\n  datasource:\n    url: jdbc:mysql://localhost:3306/sg_card?autoReconnect=true&useUnicode=true&characterEncoding=UTF-8&useSSL=false\n    driver-class-name: com.mysql.jdbc.Driver\n    username: root\n    password: \n    type: com.zaxxer.hikari.HikariDataSource\n  redis:\n    host: localhost\n    port: 6379\n  rabbitmq:\n    host: localhost\n    port: 5672\n    username: admin\n    password: admin\n    publisher-returns: true\n    publisher-confirm-type: correlated\n    listener:\n      direct:\n        acknowledge-mode: manual\n      simple:\n        acknowledge-mode: manual\n        retry:\n          initial-interval: 45000\n          max-interval: 80000\n          enabled: true\n    cache:\n      channel:\n        size: 100', 'e3e60c9ee743436c8ccb9b558478f84e', '2021-08-18 09:58:49', '2021-08-18 01:58:49', NULL, '0:0:0:0:0:0:0:1', 'D', '');
